  public function run()
  {
    $areas = collect(self::Observations)->map(function ($obs) {
      $obs['area_id'] = Area::whereName($obs['area_name'])->first()->id;
      $obs['description'] = $obs['area_name'] . ' - ' . $obs['name'];
      unset($obs['area_name']);
      return factory(Observation::class)->create($obs);
    });
